		}
		double lv=0.0,rv=1000000;
		for(int i=0;i<50;i++){
			long double mid=(lv+rv)/2.0;
			if(C(mid))rv=mid;
			else lv=mid;
		}
		printf("%.10f\n",(double)rv);
	}
	return 0;
}

