using namespace std;

struct Event{
    int time,diff;
    Event(int t,int d) : time(t), diff(d) {;}
};

bool operator<(const Event &e,const Event &f) {
    return e.time < f.time;
}

int n;
double l;
vector<Event> ev;

bool simulate(double pump) {
    double tank = l,vol = pump;
    int t = 0;

    // cout<<"pump = "<<pump<<endl;
    // cout<<tank<<endl;
    //first day
    for(int i=0; i<ev.size(); ++i) {
        tank = min(tank + vol * (ev[i].time - t), l);
        if(tank <= 0) return false;
        vol += ev[i].diff;
        t = ev[i].time;
    }
    tank = min(tank + vol * (86400 - t), l);

    // cout<<tank<<endl;
    // cout<<"end first day"<<endl;
    //second day
    double sf = tank;
    vol = pump; t = 0;

    for(int i=0; i<ev.size(); ++i) {
        tank = min(tank + vol * (ev[i].time - t), l);
        if(tank <= 0) {
            return false;
        }
        vol += ev[i].diff;
        t = ev[i].time;
    }
    tank = min(tank + vol * (86400 - t), l);

    return (tank >= sf);
}

const double eps = 1e-7;

int main() {
    int s,t,u;
    while(cin>>n>>l) {
        if(n == 0 && l == 0.0) break;
        ev.clear();
        for(int i=0; i<n; ++i) {
            cin>>s>>t>>u;
            ev.push_back(Event(s,-u));
            ev.push_back(Event(t,u));
        }

        double lo = 0.0,hi = l;
        while(lo + eps < hi) {
            double mid = (lo + hi)/2;
            bool mm;
            if(mm = simulate(mid)) hi = mid;
            else lo = mid;
            //cout<<mid<<" : "<<mm<<endl;
        }
        printf("%.6f\n", lo);
    }
}

