    double lower = 0;
    double upper = INF;
    double mid;
    while(upper - lower > EPS) {
      mid = (lower + upper) / 2.0;
      if(C(mid)) upper = mid;
      else lower = mid;
    }
    printf("%.6f\n", upper);

  }
  return 0;
}

