using namespace std;

double L,s[100],t[100],u[100];
int N;

bool simulate(double& v, double f, int i)
{
	int pt=(i==0?0:t[i-1]);
	v+=1.0*(s[i]-pt)*f;
	if(v>L) v=L;

	v+=(-u[i]+f)*(t[i]-s[i]);

	return v >= 0;
}

double bsearch(int M, double f)
{
	double hi=1e6, lo=f,mi,v;
	while(1)
	{
		mi=(hi+lo)/2;
		v=0;
		bool g=true;
		for(int i=0; i<=M; i++)
		{
			if(!simulate(v,mi,i))
			{
				g=false;
				break;
			}
		}

		if(g)	hi=mi;
		else	lo=mi;

		if(g&&(hi-lo) < 1e-8) break;
	}

	return (hi+lo)/2;
}

double calc()
{
	double f=0.0, v;
	while(1)
	{
		bool g=true;
		v=L;
		for(int i=0; i<N; i++)
		{
			if(!simulate(v,f,i))
			{
				g=false;
				f=bsearch(i,f);
				break;
			}
		}

		if(g) break; 
	}

	return f;
}

