{
	cout << setiosflags(ios::fixed) << setprecision(8);
	//ifstream cin("F.txt");

	while(cin >> N >> L, (N||L))
	{
		for(int i=0; i<N; i++)
			cin >> s[i] >> t[i] >> u[i];

		cout << calc() << endl;
	}
}

