bool C3(double v, double start) {
  double volume = start;
  double before = 0;
  rep(i, n) {
    volume += (s[i] - before) * v;
    if(volume > l) volume = l;

    double d = v - u[i];
    volume += d * (t[i] - s[i]);
    if(volume > l) volume = l;
    else if(volume < -EPS) return false;
    before = t[i];
  }
  return true;
}

bool C2(double v, double start) {
  double volume = start;
  double before = 0;
  rep(i, n) {
    volume += (s[i] - before) * v;
    if(volume > l) volume = l;

    double d = v - u[i];
    volume += d * (t[i] - s[i]);
    if(volume > l) volume = l;
    //    else if(volume < -EPS) return false;
    before = t[i];
  }
  volume += (86400-before) * v;
  return volume > start - EPS;
}

bool C(double v) {
  double lower = 0;
  double upper = l;
  double mid;
  double tl;
  while(upper - lower > EPS) {
    mid = (lower + upper) / 2.0;
    if(C3(v, mid)) upper = mid;
    else lower = mid;
    //    cout << mid << endl;
  }
  //    cout << v << " " << lower << " " << upper << endl;
  if(l == upper) return false;
  tl = lower;
  upper = l;
  while(upper - lower > EPS) {
    mid = (lower + upper) / 2.0;
    if(C2(v, mid)) lower = mid;
    else upper = mid;
  }

  return upper > tl + EPS * 10.0;
}

