  for(int i = 0; i < MAX; i++){
    tank-=cons[i];
    tank = min(tank+pump,(double)L);
    if(tank < 0 || tank == 0 && cons[i] > 0) return -1;
  }
  return tank;
}

bool isOK(double* res){

  for(int i = 0; i < 3; i++)
    if(res[i] < 0) return false;

  if(res[1] > res[2]) return false;

  return true;
}


