    double c = (l+r)/2.0;
    res[0] = L;

    for(int i = 0; i < 2; i++)
      res[i+1] = search(res[i],c);

    if(isOK(res)) r = c;
    else l = c;
  }

  printf("%.6f\n",r);
}

