bool ok(int N, int L, int s[], int t[], int u[], double m) {
  double w = L, e[2];
  for (int j = 0; j < 2; ++j) {
    for (int i = 0; i < N + 2; ++i) {
      w += (m - u[i]) * (t[i] - s[i]);
      if (w <= 0.0) {
        return false;
      }
      if (i < N + 1) {
        w += m * (s[i + 1] - t[i]);
      }
      if (w > L) w = L;
    }
    e[j] = w;
  }
  return e[1] >= e[0];
}

double calc(int N, int L, int s[], int t[], int u[]) {
  double l = 0.0, r = 1e6, m;
  for (int k = 0; k < 50; ++k) {
    if (r - l < 1e-7) break;
    m = (l + r) / 2;
    if (ok(N, L, s, t, u, m)) {
      r = m;
    } else {
      l = m;
    }
  }
  return m;
}

