using namespace std;


#define NUM 86400

struct Info{
	int start,end;
	double per_amount;
};

int N;
double L;
Info info[NUM];


bool check(double add_water){

	double Tank = min(L,info[0].start*add_water);

	for(int i = 0; i < N; i++){
		Tank -= max(0.0,info[i].per_amount-add_water)*(double)(info[i].end-info[i].start);
		if(Tank <= 0.0)break;

		if(i < N-1){
			Tank += add_water*(info[i+1].start-info[i].end);
			Tank = min(Tank,L);
		}
	}

	return Tank >= 0;
}


void func(){

	for(int i = 0; i < N; i++){
		scanf("%d %d %lf",&info[i].start,&info[i].end,&info[i].per_amount);
	}

	double left = 0,right = L, m = (left+right)/2,ans = L;

	while(right-left > EPS){
		if(check(m)){
			ans  = m;
			right = m-EPS; //最小値を求めるので、より左へ
		}else{
			left = m+EPS;
		}
		m = (left+right)/2;
	}

	printf("%.10lf\n",ans);
}

int main(){

	while(true){
		scanf("%d %lf",&N,&L);
		if(N == 0)break;

		func();
	}

	return 0;
}

