using namespace std;
#define REP(i,b,n) for(int i=b;i<n;i++)
#define rep(i,n)   REP(i,0,n)
const double eps = 1e-7;
class Info{
public:
  int s,e,c,time;
  double consume;
  Info(){};
  Info(int ts,int te,int tc):s(ts),e(te),c(tc){
    consume=e-s;
    consume*=(double)c;
    time=e-s;
  };
  bool operator<(const Info &a)const {
    return s<a.s;
  }
};
bool can_water(vector<Info >&in,double L,double p){
  double vol=0;
  int lasttime=0;//last time that scheduled
  rep(i,in.size()){
    if (in[i].s!=0 && vol+(in[i].s-lasttime)*p<L)return false;
    vol=min(L,vol+(in[i].s-lasttime)*p);
    vol=vol+in[i].time*p-in[i].consume;
    if ( vol<0)return false;
    vol=min(vol,L);
    lasttime=in[i].e;
  }
  return true;
}

double solve(vector<Info > &in,double L){
  sort(in.begin(),in.end());
  double mini=0,maxi=1000000,ret=0,mid;
  while(mini+eps<maxi){
    double mid = (mini+maxi)/2;
    if ( can_water(in,L,mid))ret=mid,maxi=mid-eps;
    else mini=mid+eps;
  }
  return ret;
}

main(){
  int n,L;
  while(cin>>n>>L&&n){
    vector<Info> in;
    int s,t,c;
    rep(i,n){
      cin>>s>>t>>c;
      in.push_back(Info(s,t,c));
    }
    printf("%.6lf\n",solve(in,L));
  }
}

