using namespace std;

struct Event{
    int time,diff;
    Event(int t,int d) : time(t), diff(d) {;}
};

bool operator<(const Event &e,const Event &f) {
    return e.time < f.time;
}

int n;
double l;
vector<Event> ev;

bool simulate(double pump) {
    double tank = l,vol = pump;
    int t = 0;

    // cout<<"pump = "<<pump<<endl;
    // cout<<tank<<endl;
    //first day
    for(int i=0; i<ev.size(); ++i) {
        tank = min(tank + vol * (ev[i].time - t), l);
        if(tank <= 0) return false;
        vol += ev[i].diff;
        t = ev[i].time;
    }
    tank = min(tank + vol * (86400 - t), l);

    // cout<<tank<<endl;
    // cout<<"end first day"<<endl;
    //second day
    bool full = (tank >= l);
    vol = pump; t = 0;

    for(int i=0; i<ev.size(); ++i) {
        tank = min(tank + vol * (ev[i].time - t), l);
        if(tank <= 0) {
            return false;
        }
        if(tank >= l) full = true;
        vol += ev[i].diff;
        t = ev[i].time;
    }

    return full;
}

