            cin>>s>>t>>u;
            ev.push_back(Event(s,-u));
            ev.push_back(Event(t,u));
        }

        double lo = 0.0,hi = l;
        while(lo + eps < hi) {
            double mid = (lo + hi)/2;
            bool mm;
            if(mm = simulate(mid)) hi = mid;
            else lo = mid;
            //cout<<mid<<" : "<<mm<<endl;
        }
        printf("%.6f\n", lo);
    }
}

