def check(h, slist, l):
    last = l
    t = 0
    for s in slist:
        last += (s[0] - t) * h
        if last > l:
            last = l
        last -= (s[1] - s[0]) * (s[2]-h)
        t = s[1]  
        if last < 0:
            return False
        if last > l:
            last = l
    d1last = last
    t -= 86400
    for s in slist:
        last += (s[0] - t) * h
        if last > l:
            last = l
        last -= (s[1] - s[0]) * (s[2]-h)
        t = s[1]  
        if last < 0:
            return False
        if last > l:
            last = l
    if last < d1last:
        return False
    return True
while True:
    N, L = map(int, input().split())
