			if(EQ(p,0))m[0]=m[1]=m[2]=0;
			else
			{
				r=sqrt(-4*p/3);
				if(EQ(((r+a)*r+b)*r+c,0))m[0]=r, m[1]=m[2]=r*cos(2*pi/3);
				else m[0]=-r;m[1]=m[2]=r*cos(pi/3);
			}
			n=3;
		}
		else if(d>0)
		{
			r=sqrt(-4*p/3);
			double x=acos(3*q/p/r)/3;
			m[0]=r*cos(x),m[1]=r*cos(2*pi/3-x),m[2]=r*cos(2*pi/3+x);
			n=3;
		}
		else
		{
			double x=sqrt(-d);
			m[0]=pow(-q/2+x,1./3)+pow(-q/2-x,1./3);
			n=1;
		}
		for(int i=0;i<n;++i)
		{
			m[i]-=a/3;
			if(!EQ(m[i],0))
			{
				if(m[i]>0)++s;
				else ++t;
			}
		}
		printf("%d %d\n",s,t);
	}
	return 0;
}

