long double a, b, c, d; int n;

long double calc(long double pos) {
	return a * pos * pos * pos + b * pos * pos + c * pos + d;
}

long double find_answer(long double L, long double R, bool ok) {
	long double p1 = calc(L), p2 = calc(R); if (p1 > p2) swap(p1, p2);
	if (!(p1 <= 1e-14 && -1e-14 <= p2)) return -1e9;

	long double M;
	for (int i = 0; i < 100; i++) {
		M = (L + R) / 2;
		long double ret = calc(M);
		bool res = false; if (ret > 0) res = true;

		if ((ok ^ res) == false) R = M;
		else L = M;
	}
	return M;
}

int main() {
