		if (B * B - 4.0 * A * C < -1e-5) {
			T.push_back(make_tuple(-10000, 10000, true));
		}
		else {
			long double V = sqrtl(B * B - 4.0 * A * C);
			long double W1 = (-B - V) / (2.0 * A);
			long double W2 = (-B + V) / (2.0 * A);
			T.push_back(make_tuple(-10000, W1, true));
			T.push_back(make_tuple(W1, W2, false));
