double fx(double x){
    return a*x*x*x+b*x*x+c*x+d;
}

bool binary(double l, double r, double &v){
    if(same(fx(l), 0)){
        v = l;
        return true;
    }
    if(same(fx(r), 0)){
        v = r;
        return true;
    }
    if(fx(l)*fx(r) > 0) return false;
    int loop = 200;
    while(loop-- > 0){
        double mid = (l+r)/2;
        double val = fx(mid);
        if(val*fx(l) > 0)   l = mid;
        else                r = mid;
    }
    if(same(fx(l),0)){
        v = l;
        return true;
    }
    if(same(fx(r),0)){
        v = r;
        return true;
    }
    return false;
}


