using ld = long double;
const ld eps = 1e-10;

ld bisectionMethod(ld x0, ld x1, function<ld(ld)> f){
    ld y0 = f(x0), y1 = f(x1);
    int s0 = fabs(y0) < eps ? 0 : y0 < 0 ? -1 : 1;
    int s1 = fabs(y1) < eps ? 0 : y1 < 0 ? -1 : 1;
    assert(s0 * s1 <= 0);
    if(y0 > y1) swap(x0, x1);
    ld m;
    for(int i = 0; i < 200; i++){
        m = (x0 + x1) / 2;
        if(f(m) > 0) x1 = m;
        else x0 = m;
    }
    return m;
}

vector<ld> quadraticEquation(ld a, ld b, ld c){
    ld d = b*b - 4*a*c;
    if(fabs(a) < eps) return fabs(b) > eps ? vector<ld>{ -c/b } : vector<ld>{};
    if(d < -eps) return {};
    if(d < eps) return { -b / (2*a), -b / (2*a) };
    int sign = (b >= 0) ? 1 : -1;
    ld x0 = (-b - sign * sqrt(abs(d))) / (2*a), x1 = c / (a*x0);
    return { x0, x1 };
}

vector<ld> cubicEquation(ld a, ld b, ld c, ld d){
    if(fabs(a) < eps) return quadraticEquation(b,c,d);
    if(a < 0) a = -a, b = -b, c = -c, d = -d;
    ld x0 = bisectionMethod(-1e8, 1e8, [&](ld x){
            return a*x*x*x + b*x*x + c*x + d;
        });
    ld A = 1, B = b/a + x0, C = c/a + B*x0;
    dump(A,B,C);
    auto res = quadraticEquation(A,B,C);
    dump(res);
    res.push_back(x0);
    sort(res.begin(), res.end());
    return res;
}

