pair<long double, long double> kai2(long double ax, long double bx, long double cx) {
	bx /= ax; cx /= ax; ax = 1.0l;
	long double D = bx*bx - 4.0l*ax*cx;
	if (D < 0) { return make_pair(-1e9, -1e9); }
	long double E = sqrtl(D);
	long double F = (-1.0l * bx + E) / 2.0l;
	long double G = (-1.0l * bx - E) / 2.0l;
	return make_pair(G, F);
}
