#include<bits/stdc++.h>
using namespace std;
double cuberoot(double x){
  double s, prev;
  int positive;
  if(x == 0) return 0;
  if(x > 0){
    positive = 1;
  }else{
    positive = 0;
    x = -x;
  }
  if(x > 1){
    s = x;
  } else {
    s = 1;
  }
  do {
    prev = s;
    s = (x / (s * s) + 2 * s) / 3;
  } while(s < prev);
  if(positive) return prev;
  else return -prev;
}
vector< double > cardano(double a, double b, double c, double d){
  double p, q, t, a3, b3, x1, x2, x3;
  vector< double > ret;

  b /= 3 * a;
  c /= a;
  d /= a;
  p = b * b - c / 3;
  q = (b * (c - 2 * b * b) - d) / 2;
  a = q * q - p * p * p;
  if(a == 0){
    q = cuberoot(q);
    x1 = 2 * q - b;
    x2 = -q - b;

    ret.push_back(x1), ret.push_back(x2), ret.push_back(x2);
  } else if(a > 0){
    if(q > 0) a3 = cuberoot(q + sqrt(a));
    else a3 = cuberoot(q - sqrt(a));
    b3 = p / a3;
    x1 = a3 + b3 - b;
    x2 = -0.5 * (a3 + b3) - b;
    x3 = fabs(a3 - b3) * sqrt(3.0) / 2;

    ret.push_back(x1);
  } else {
    a = sqrt(p);
    t = acos(q / (p * a));
    a *= 2;
    x1 = a * cos(t / 3) - b;
    x2 = a * cos((t + 2 * M_PI) / 3) - b;
    x3 = a * cos((t + 4 * M_PI) / 3) - b;

    ret.push_back(x1), ret.push_back(x2), ret.push_back(x3);
  }
  return ret;
}

