    if(s<0){
      s=abs(s);
      s=pow(s,1.0/3);
      s*=-1;
    }
    else s=pow(s,1.0/3);
    double x1=r+s-1.0*a/3;
    P x2=P(-0.5,sqrt(3.0)/2)*P(r,0)+
      P(-0.5,-sqrt(3.0)/2)*P(s,0)-1.0*a/3;
    P x3=P(-0.5,-sqrt(3.0)/2)*P(r,0)+
      P(-0.5,sqrt(3.0)/2)*P(s,0)-1.0*a/3;
    int pos=0;
    int neg=0;
    if(!EQ(x1,0)){
      if(x1>0)pos++;
      else neg++;
    }
    if(!EQ(x2.real(),0)&&EQ(x2.imag(),0)){
      if(x2.real()>0)pos++;
      else neg++;
    }
    if(!EQ(x3.real(),0)&&EQ(x3.imag(),0)){
      if(x3.real()>0)pos++;
      else neg++;
    }
    cout<<pos<<" "<<neg<<endl;
  }  

  return 0;
}

