#include<cmath>
#include<cstdio>

#define	f(x)	((((a*x)+b)*x+c)*x+d)

using namespace std;

const double inf=1e9,eps=1e-9;
int a,b,c,d;

double bisection_method(double l,double r){
	double m,fm;
	while(1){
		m=(l+r)/2;
		fm=f(m);
		if(fm<-eps)		l=m;
		else if(eps<fm)	r=m;
		else	return m;
	}
}

int main(){
	int n;	scanf("%d",&n);
	while(n--){
		scanf("%d%d%d%d",&a,&b,&c,&d);
		if(a<0)	a=-a,b=-b,c=-c,d=-d;	// f(x) = a x^3 + b x^2 + c x + d = 0

		int aa=3*a,bb=2*b,cc=c;	// derivative : f'(x) = aa x^2 + bb x + cc
		int D=bb*bb-4*aa*cc;	// discriminant
		int posi=0,nega=0;
		if(D<0){	// f(x) = 0 has 1 root
			double x=bisection_method(-inf,inf);
			if(eps<x)	posi++;
			if(x<-eps)	nega++;
		}
		else if(D==0){
			double x=-bb/(2.*aa);
			if(abs(f(x))<eps){ // 3-fold root
				if(eps<x)	posi+=3;
				if(x<-eps)	nega+=3;
			}
			else{	// 1 root
				double x=bisection_method(-inf,inf);
				if(eps<x)	posi++;
				if(x<-eps)	nega++;
			}
		}
		else{
			double x1=(-b-sqrt(D))/(2.*a),x2=(-b+sqrt(D))/(2.*a);
			if(f(x1)<-eps){
				double x=bisection_method(-inf,inf);
				if(eps<x)	posi++;
				if(x<-eps)	nega++;
			}
			else if(abs(f(x1))<eps){
				if(eps<x1)	posi+=2;
				if(x1<-eps)	nega+=2;
				double x=bisection_method(x2,inf);
				if(eps<x)	posi++;
				if(x<-eps)	nega++;
			}
			else if(eps<f(x1) && f(x2)<-eps){
				double x=bisection_method(-inf,x1);
				if(eps<x)	posi++;
				if(x<-eps)	nega++;
				x=bisection_method(x1,x2);
				if(eps<x)	posi++;
				if(x<-eps)	nega++;
				x=bisection_method(x2,inf);
				if(eps<x)	posi++;
				if(x<-eps)	nega++;
			}
			else if(abs(f(x2))<eps){
				if(eps<x2)	posi+=2;
				if(x2<-eps)	nega+=2;
				double x=bisection_method(-inf,x1);
				if(eps<x)	posi++;
				if(x<-eps)	nega++;
			}
			else{
				double x=bisection_method(-inf,inf);
				if(eps<x)	posi++;
				if(x<-eps)	nega++;
			}
		}

		printf("%d %d\n",posi,nega);
	}

	return 0;
}

