#include<algorithm>
using namespace std;

bool sol;

pair<double,double> square(int k1, int k2, int k3){
  pair<double,double> res;
  int root = k2*k2 - 3*k1*k3;
  //cout << root << endl;
  if(root < 0){
    sol = false;
    res.first = 0.0;
    res.second = 0.0;
  }else if(root > 0){
    sol = true;
    res.first = (-1.0*k2 + sqrt(root)) / (3.0*k1);
    res.second = (-1.0*k2 - sqrt(root)) / (3.0*k1);
    if(res.first < res.second)swap(res.first,res.second);
  }else{
    sol = false;
    res.first = res.second = (-1.0*k2) / (3.0*k1);
  }
  return res;
}

int cubic(int k1,int k2,int k3,int k4){
  if(k2*k2*k3*k3 - 4*k1*k3*k3*k3 - 4*k2*k2*k2*k4 - 27*k1*k1*k4*k4 + 18*k1*k2*k3*k4 < 0)return 1;
  else return 3;
}

