	if(s1<0.0){
	  neg = 2;
	  if(d<0)pos++;
	  if(d>0)neg++;
	}else if(s2>0.0){
	  pos = 2;
	  if(d<0)pos++;
	  if(d>0)neg++;
	}else{
	  pos=1;
	  neg=1;
	  if(d<0)neg++;
	  if(d>0)pos++;
	}
