using namespace std;
long double a, b, c, d;
vector<pair<long double, long double>>vec;
int cnt1, cnt2;
long double kai(long double x) {
	return x*x*x*a + x*x*b + x*c + d;
}
pair<long double, long double> kai2(long double ax, long double bx, long double cx) {
	bx /= ax; cx /= ax; ax = 1.0l;
	long double D = bx*bx - 4.0l*ax*cx;
	if (D < 0) { return make_pair(-1e9, -1e9); }
	long double E = sqrtl(D);
	long double F = (-1.0l * bx + E) / 2.0l;
	long double G = (-1.0l * bx - E) / 2.0l;
	return make_pair(G, F);
}
int main() {
	int n; cin >> n;
	for (int h = 0; h < n; h++) {
		cin >> a >> b >> c >> d; vec.clear(); cnt1 = 0; cnt2 = 0;
		b /= a; c /= a; d /= a; a = 1.0l;
		for (long double i = -101.00031415926l; i <= 101.0l; i += 1e-3) {
			vec.push_back(make_pair(kai(i), i));
		}
		long double l = 1e9, r = 1e9, m; int type = 0;
		for (int i = 0; i < vec.size() - 1; i++) {
			if (vec[i].first < 0.0l && vec[i + 1].first > 0.0l) { l = vec[i].second; r = vec[i + 1].second; type = 1; }
			if (vec[i].first > 0.0l && vec[i + 1].first < 0.0l) { l = vec[i].second; r = vec[i + 1].second; type = 2; }
		}
		if (type == 1) {
			for (int i = 0; i < 1000; i++) {
				m = (l + r) / 2.0l;
				if (kai(m) < 0.0l)l = m;
				if (kai(m) > 0.0l)r = m;
			}
		}
		if (type == 2) {
			for (int i = 0; i < 1000; i++) {
				m = (l + r) / 2.0l;
				if (kai(m) < 0.0l)r = m;
				if (kai(m) > 0.0l)l = m;
			}
		}
		if (fabs(m) < 1e-50)type = 0;
		if (type == 0) { cout << "0 0" << endl; continue; }
		long double a1 = a, b1 = b, c1 = c, d1 = d;
		long double e1 = a1; a1 -= e1; b1 += m*e1;
		long double e2 = b1; b1 -= e2; c1 += m*e2;
		long double e3 = c1; c1 -= e3; d1 += m*e3;

		long double ans1 = m;
		long double ans2 = kai2(e1, e2, e3).first;
		long double ans3 = kai2(e1, e2, e3).second;

		if (fabs(ans1) > 1e-8) { if (ans1 > 0.0l)cnt1++; if (ans1 < 0.0l)cnt2++; }
		if (fabs(ans2) > 1e-8) { if (ans2 > 0.0l)cnt1++; if (ans2 < 0.0l)cnt2++; }
		if (fabs(ans3) > 1e-8) { if (ans3 > 0.0l)cnt1++; if (ans3 < 0.0l)cnt2++; }

		cout << cnt1 << ' ' << cnt2 << endl;
	}
	return 0;
}

