const  double eps = 1.0e-5;

using namespace std;

int main()
{
  int N;
  cin >> N;
  for(int tc = 0; tc < N; ++tc){
    int pos_ans = 0, neg_ans = 0;
    int a3,a2,a1,a0;
    cin >> a3 >> a2 >> a1 >> a0;
    double A2,A1,A0;
    A2=(double)a2/a3;
    A1=(double)a1/a3;
    A0=(double)a0/a3;

    complex<double> p(A1-(pow(A2,2.0)/3.0),0);
    complex<double> q(A0-(1.0/3.0)*A1*A2+(2.0/27.0)*pow(A2,3.0),0);
    complex<double> omega(-0.5,sqrt(3.0)/2.0);
    complex<double> y[3],x[3];

    for(int k = 0; k < 3; ++k){
      y[k] = 
	pow(omega,k)*pow(-q/2.0+pow(pow(q/2.0,2.0)+pow(p/3.0,3.0),0.5),(1.0/3.0))+
	pow(omega,3-k)*pow(-q/2.0-pow(pow(q/2.0,2.0)+pow(p/3.0,3.0),0.5),(1.0/3.0));
      x[k]=y[k]-complex<double>(A2/3.0,0);
      //cout << x[k] << endl;
      if(abs(x[k].imag())<eps){
	if(x[k].real()>0){
	  ++pos_ans;
	}else if(x[k].real()<0){
	  ++neg_ans;
	}
      }
    }
    cout << pos_ans << ' ' << neg_ans << endl;
  }
  return 0;
}

