using namespace std;

const double inf=1e9,eps=1e-9;
int a,b,c,d;

double bisection_method(double l,double r){
	double m,fm,fl,fr;
	while(1){
		m=(l+r)/2;
		fl=f(l);	fr=f(r);	fm=f(m);
		if(abs(fm)<eps)	return m;
		if((fm<-eps && fl<-eps)||(eps<fm && eps<fl))	l=m;
		else	r=m;
	}
}

