		int aa=3*a,bb=2*b,cc=c;
		int D=bb*bb-4*aa*cc;
		int posi=0,nega=0;
		if(D<0){
			double x=bisection_method(-inf,inf);
			if(eps<x)	posi++;
			if(x<-eps)	nega++;
		}
		else if(D==0){
			double x=-bb/(2.*aa);
			if(abs(f(x))<eps){
				if(eps<x)	posi+=3;
				if(x<-eps)	nega+=3;
			}
			else{
				double x=bisection_method(-inf,inf);
				if(eps<x)	posi++;
				if(x<-eps)	nega++;
			}
		}
		else{
			double x1=(-bb-sqrt(D))/(2.*aa),x2=(-bb+sqrt(D))/(2.*aa);
			if(f(x1)<-eps){
				double x=bisection_method(-inf,inf);
				if(eps<x)	posi++;
				if(x<-eps)	nega++;
			}
			else if(abs(f(x1))<eps){
				if(eps<x1)	posi+=2;
				if(x1<-eps)	nega+=2;
				double x=bisection_method(x2,inf);
				if(eps<x)	posi++;
				if(x<-eps)	nega++;
			}
			else if(eps<f(x1) && f(x2)<-eps){
				double x=bisection_method(-inf,x1);
				if(eps<x)	posi++;
				if(x<-eps)	nega++;
				x=bisection_method(x1,x2);
				if(eps<x)	posi++;
				if(x<-eps)	nega++;
				x=bisection_method(x2,inf);
				if(eps<x)	posi++;
				if(x<-eps)	nega++;
			}
			else if(abs(f(x2))<eps){
				if(eps<x2)	posi+=2;
				if(x2<-eps)	nega+=2;
				double x=bisection_method(-inf,x1);
				if(eps<x)	posi++;
				if(x<-eps)	nega++;
			}
			else{
				double x=bisection_method(-inf,inf);
				if(eps<x)	posi++;
				if(x<-eps)	nega++;
			}
		}

		printf("%d %d\n",posi,nega);
	}

	return 0;
}

