double bisection_method(double ng, double ok, function<double(double)> f) {
    assert(f(ng) == false);
    assert(f(ok) == true);
    for (int i = 0; i < 100; i++) {
        double mid = (ng + ok) / 2;
        (f(mid) ? ok : ng) = mid;
    }
    return ok;
}

vector<double> quadratic_equation(double a, double b, double c) {
    // ax^2 + bx + c = 0
    if (a == 0) {
        if (b == 0) return {};
        return { -c / b };
    }
    double d = b*b - 4*a*c;
    if (d < -eps) return {};                            // D < 0
    if (d < eps) return { -b / (2*a), -b / (2*a) };     // D == 0
    return { (-b + sqrt(d)) / (2*a), (-b - sqrt(d)) / (2*a) };
}

vector<double> cubic_equation(double a, double b, double c, double d) {
    // ax^3 + bx^2 + cx + d = 0
    if (a == 0) return quadratic_equation(b, c, d);
    if (a < 0) a = -a, b = -b, c = -c, d = -d;
    // (x - x0)(sx^2 + tx + u)
    // s = a, t = b + s*x0, u = c + t*x0 = -d/x0 <- x0 = 0 があるので使えない
    double x0 = bisection_method(-1e9, 1e9, [&](double x) {
        return a*x*x*x + b*x*x + c*x + d > 0;
    });
    vector<double> res = quadratic_equation(a, b + a*x0, c + b*x0 + a*x0*x0);
    res.push_back(x0);
    return res;
}

