        vector<double> xs = cubic_equation(a, b, c, d);
        int pos = 0, neg = 0;
        for (double x : xs) {
            if (x > eps) pos++;
            if (x < -eps) neg++;
        }
        cout << pos << " " << neg << endl;
    }
    return 0;
}

