	while(T--){
		int a, b, c, d;
		scanf("%d%d%d%d",&a,&b,&c,&d);
		pair<int,int> ans = solve(a,b,c,d);
		printf("%d %d\n", ans.first, ans.second);
	}
	return 0;
}

