using namespace std;


#define BIG_NUM 1000000

int A,B,C,D;
int num_plus,num_minus;
double d_A,d_B,d_C,d_D;

double calc(double x){
	return d_A*x*x*x + d_B*x*x + d_C*x + d_D;
}

void count_ans(double x){

	if(fabs(x) < EPS){
		return;
	}

	if(x > 0){

		num_plus += 1;

	}else{

		num_minus += 1;
	}
}

void find_ans(double left,double right,bool increase){

	if(fabs(calc(left)) < EPS){

		count_ans(left);
		return;

	}else if(fabs(calc(right)) < EPS){

		count_ans(right);
		return;
	}

	double L = left,R = right,mid = (L+R)/2.0;

	while(L <= R){

		if(fabs(calc(mid)) < EPS){

			count_ans(mid);
			return;

		}else if(calc(mid) > 0){

			if(increase){

				R -= EPS;

			}else{

				L += EPS;
			}
		}else{

			if(increase){

				L += EPS;

			}else{

				R -= EPS;
			}
		}
		mid = (L+R)/2.0;
	}
}

void func(){

	num_plus = 0;
	num_minus = 0;

	scanf("%d %d %d %d",&A,&B,&C,&D);

	d_A = (double)A;
	d_B = (double)B;
	d_C = (double)C;
	d_D = (double)D;

	int discriminant = B*B-3*A*C;

	if(discriminant >= 0){

		double mid_1 = (-d_B-sqrt((double)discriminant))/(3.0*d_A);
		double mid_2 = (-d_B+sqrt((double)discriminant))/(3.0*d_A)+EPS;

		find_ans(-BIG_NUM,mid_1,calc(-BIG_NUM) < calc(mid_1));
		find_ans(mid_1,mid_2,calc(mid_1) < calc(mid_2));
		find_ans(mid_2,BIG_NUM,calc(mid_2) < calc(BIG_NUM));

	}else{

		find_ans(-BIG_NUM,BIG_NUM,calc(-BIG_NUM) < calc(BIG_NUM));
	}

	printf("%d %d\n",num_plus,num_minus);
}

