using namespace std;

int n, a, b, c, d;
double A, B, C, E = 1e-10;

double f(double x) {
  return a * x * x * x + b * x * x + c * x + d;
}

int main() {
  scanf("%d", &n);
  for (int i = 0; i < n; i++) {
    int M = 0, P = 0;
    scanf("%d%d%d%d", &a, &b, &c, &d);
    if (d != 0) {
      double minas = 1000.0, plus = -1000.0;
      if (f(minas) > 0.0) minas = -1000.0, plus = 1000.0;
      double mid = 0.0;
      while (abs(f(mid)) > E) {
        if (f(mid) < E) minas = mid;
        else plus = mid;
        mid = (minas + plus) / 2.0;
      }
      if (mid > E) P++;
      else if (mid < -E) M++;
      A = (double)a, B = (double)b + mid * A, C = (double)c + mid * B;
    } else {
      A = (double)a, B = (double)b, C = (double)c;
    }
    double D = B * B - 4 * A * C;
    if (D > E) {
      double d2 = sqrt(D);
      if (A*(d2 - B) > E) P++;
      if (A*(d2 - B) < -E) M++;
      if (A*(-d2 - B) > E) P++;
      if (A*(-d2 - B) < -E) M++;
    } else if (D > -1e-4) {
      if (A*(-B) > E) P += 2;
      if (A*(-B) < -E) M += 2;
    }
    printf("%d %d\n", P, M);
  }
}

