#include <iostream>
#include <cmath>
#include <utility>
using namespace std;
const double EPS = 1e-5;

double f(double a, double b, double c, double d, double x){
  return a*x*x*x + b*x*x + c*x + d;
}

pair<int, int> solve(double a, double b, double c, double d){
  if(b*b - 3*a*c < 0){
    if(d > EPS) return {0,1};
    else if(d < -EPS) return {1,0};
  }
  double s = (-b - sqrt(b*b - 3*a*c))/3/a, t = (-b + sqrt(b*b - 3*a*c))/3/a;
  //cout << s << " " << t << endl;

  if(abs(d) < EPS && abs(s) < EPS && abs(t) < EPS)
    return {0, 0};

  int p = 0, n = 0;
  //cerr << f(a,b,c,d,s) << endl;
  if(t < -EPS){
    if(f(a,b,c,d,0) < -EPS) ++p;
    else if(d > EPS) ++n;
    if(f(a,b,c,d,s)*f(a,b,c,d,t) <= EPS) n += 2;
  }else if(s > EPS){
    if(f(a,b,c,d,0) < -EPS) ++p;
    else if(d > EPS) ++n;
    if(f(a,b,c,d,s)*f(a,b,c,d,t) <= EPS) p += 2;
  }else{
    if(f(a,b,c,d,s) >= -EPS) ++n;
    if(f(a,b,c,d,t) <= EPS) ++p;
    //cout << f(a,b,c,d,s)*f(a,b,c,d,t) << endl;
    //cout << f(a,b,c,d,t) << endl;
    if(f(a,b,c,d,s)*f(a,b,c,d,t) <= EPS){
      if(f(a,b,c,d,0) > EPS) ++p;
      else if(d < -EPS) ++n;
    }
  }
  return make_pair(p, n);
}

int main(){
  int n;
  cin >> n;
  for(int i = 0; i < n; ++i){
    double a, b, c, d;
    cin >> a >> b >> c >> d;
    if (a < 0) {
      a = -a;
      b = -b;
      c = -c;
      d = -d;
    }
    pair<int,int> ans = solve(a,b,c,d);
    cout << ans.first << " " << ans.second << endl;
  }
  return 0;
}

