using namespace std;
typedef long long ll;
typedef unsigned int uint;
typedef unsigned long long ull;
static const double EPS = 1e-9;
static const double PI = acos(-1.0);

#define REP(i, n) for (int i = 0; i < (int)(n); i++)
#define FOR(i, s, n) for (int i = (s); i < (int)(n); i++)
#define FOREQ(i, s, n) for (int i = (s); i <= (int)(n); i++)
#define FORIT(it, c) for (__typeof((c).begin())it = (c).begin(); it != (c).end(); it++)
#define MEMSET(v, h) memset((v), h, sizeof(v))

double calcPoly(const vector<double> &poly, double x) {
  double ret = 0.0;
  double base = 1;
  REP(i, poly.size()) {
    ret += poly[i] * base;
    base *= x;
  }
  return ret;
}

// find max point [left, right]
double ts(const vector<double> &poly, double left, double right) {
  REP(iter, 300) {
    double lmid = (left * 2.0 + right) / 3.0;
    double rmid = (left + right * 2.0) / 3.0;
    double lans = calcPoly(poly, lmid);
    double rans = calcPoly(poly, rmid);
    assert(!isnan(lans) && !isnan(rans));
    if (lans < rans) {
      left = lmid;
    } else {
      right = rmid;
    }
  }
  return left;
}

// find 0 point [left, right]
// poly must monotonic increase in [left, right]
double bs(const vector<double> &poly, double left, double right) {
  REP(iter, 200) {
    double mid = (left + right) / 2.0;
    double ans = calcPoly(poly, mid);
    if (ans < 0) {
      left = mid;
    } else {
      right = mid;
    }
  }
  return left;
}

vector<double> differential(const vector<double> &poly) {
  vector<double> ret(poly.size() - 1, 0);
  FOR(i, 1, poly.size()) {
    ret[i - 1] = poly[i] * i;
  }
  return ret;
}

vector<double> minusPoly(const vector<double> &poly) {
  vector<double> ret(poly.size(), 0);
  REP(i, poly.size()) { ret[i] = -poly[i]; }
  return ret;
}

void printPoly(const vector<double> &poly) {
  for (int i = poly.size() - 1; i >= 0; i--) {
    cout << poly[i] << " ";
  }
  cout << endl;
}

char str[1000];
int main() {
  int test;
  scanf("%d", &test);
  while (test--) {
    vector<double> poly(4, 0);
    scanf("%lf %lf %lf %lf", &poly[3], &poly[2], &poly[1], &poly[0]);
    assert(-100 <= poly[0] && poly[0] <= 100);
    assert(-100 <= poly[1] && poly[1] <= 100);
    assert(-100 <= poly[2] && poly[2] <= 100);
    assert(-100 <= poly[3] && poly[3] <= 100);
    assert(poly[3] != 0);
    if (poly[3] < 0) { poly = minusPoly(poly); }
    vector<double> dif = minusPoly(differential(poly));
    double center = ts(dif, -1000, 1000);
    double leftLocalMax = ts(poly, -1000, center);
    double rightLocalMin = ts(minusPoly(poly), center, 1000);
    double ans[3];
    ans[0] = bs(poly, -1000, leftLocalMax);
    ans[1] = bs(minusPoly(poly), leftLocalMax, rightLocalMin);
    ans[2] = bs(poly, rightLocalMin, 1000);
    int minusans = 0;
    int plusans = 0;
    REP(i, 3) {
      if (fabs(calcPoly(poly, ans[i])) < EPS) {
        if (ans[i] < -EPS) { minusans++; }
        if (ans[i] > EPS) { plusans++; }
      }
    }
    printf("%d %d\n", plusans, minusans);
  }
  assert(scanf("%s", str) == EOF);
}

