using namespace std;

bool sol;

pair<double,double> square(int k1, int k2, int k3){
  pair<double,double> res;
  int root = k2*k2 - 3*k1*k3;
  if(root < 0){
    sol = false;
    res.first = 0.0;
    res.second = 0.0;
  }else if(root > 0){
    sol = true;
    res.first = (-1.0*k2 + sqrt(root)) / (3.0*k1);
    res.second = (-1.0*k2 - sqrt(root)) / (3.0*k1);
    if(res.first < res.second)swap(res.first,res.second);
  }else{
    sol = false;
    res.first = res.second = (-1.0*k2) / (3.0*k1);
  }
  return res;
}

int cubic(int k1,int k2,int k3,int k4){
  if(k2*k2*k3*k3 - 4*k1*k3*k3*k3 - 4*k2*k2*k2*k4 - 27*k1*k1*k4*k4 + 18*k1*k2*k3*k4 < 0)return 1;
  else return 3;
}

int main(){
  int a,b,c,d;
  int t;
  int pos,neg;

  cin >> t;
  while(t--){
    cin >> a >> b >> c >> d;
    pos = neg = 0;

    pair<double,double> k;
    k = square(a,b,c);

    if(!sol){
      int tmp = cubic(a,b,c,d);
      if(a>0){
	if(d<0)pos=tmp;
	if(d>0)neg=tmp;
      }else{
	if(d<0)neg=tmp;
	if(d>0)pos=tmp;
      }
    }else{
      double s1 = k.first, s2 = k.second;
      double v1 = a*s1*s1*s1 + b*s1*s1 + c*s1 + d;
      double v2 = a*s2*s2*s2 + b*s2*s2 + c*s2 + d;
      if(v1*v2 > 0.0){
	if(a>0){
	  if(s2<0.0)neg=1;
	  else if(d<0)pos=1;
	  else if(d>0)neg=1;
	}else{
	  if(s1>0.0)pos=1;
	  else if(d<0)neg=1;
	  else if(d>0)pos=1;
	}
      }else if(v1*v2 < 0.0){
	if(s1<0.0){
	  neg = 2;
	  if(d<0)pos++;
	  if(d>0)neg++;
	}else if(s2>0.0){
	  pos = 2;
	  if(d<0)pos++;
	  if(d>0)neg++;
	}else{
	  pos=1;
	  neg=1;
	  if(d<0)neg++;
	  if(d>0)pos++;
	}
      }else{
	if(abs(v1) < 1e-10){
	  if(s1>0.0)pos=2;
	  if(s1<0.0)neg=2;
	  if(s2>0.0){
	    if(a>0){
	      if(d<0)pos++;
	      if(d>0)neg++;
	    }else{
	      if(d<0)neg++;
	      if(d>0)pos++;
	    }
	  }
	  if(s2<0.0)neg++;
	}
	if(abs(v2) < 1e-10){
	  if(s2>0.0)pos=2;
	  if(s2<0.0)neg=2;
	  if(s1<0.0){
	    if(a>0){
	      if(d<0)pos++;
	      if(d>0)neg++;
	    }else{
	      if(d<0)neg++;
	      if(d>0)pos++;
	    }
	  }
	  if(s1>0.0)neg++;
	}
      }
    }
    cout << pos << " " << neg << endl;
  }
}

