    double x = (ub + lb) / 2;
    if(f(x) > 0){
      ub = x;
    }else if(f(x) < 0){
      lb = x;
    }
  }
  double x1 = (ub + lb) / 2;
  double A = 1;
  double B = b/a + x1;
  double C = c/a + B * x1;
  vector<double> ans = quadratic(A, B, C);
  ans.push_back(x1);
  return ans;
}

