const long double pi = acos(-1.0);
int Q; long double a, b, c, d;
int main() {
	cin >> Q;
	while (Q--) {
		cin >> a >> b >> c >> d;
		complex<long double> z1(b * b - 3 * a * c, 0);
		complex<long double> z2(2 * b * b * b - 9 * a * b * c + 27 * a * a * d, 0);
		complex<long double> z3 = z2 * z2 - 4.0L * z1 * z1 * z1;
		complex<long double> zp = (z2 + sqrt(z3)) / 2.0L;
		vector<complex<long double> > v;
		long double r = pow(abs(zp), 1.0 / 3.0), t = arg(zp);
		for (int j = 0; j < 3; j++) {
			v.push_back(complex<long double>(r * cos(t), r * sin(t)));
			t += pi * 2 / 3;
		}
		for (int i = 0; i < v.size(); i++) {
			if (v[i] != 0.0L) v[i] = -(b + v[i] + z1 / v[i]) / (3 * a);
			else v[i] = -b / (3 * a);
		}
		int ret1 = 0, ret2 = 0;
		for (int i = 0; i < v.size(); i++) {
			if (abs(imag(v[i])) < 1.0e-10L) {
				if (real(v[i]) > 1.0e-10L) ret1++;
				if (real(v[i]) < -1.0e-10L) ret2++;
			}
		}
		cout << ret1 << ' ' << ret2 << endl;
	}
	return 0;
}

