const double EPS=1e-9;
bool EQ(double a,double b){
  return abs(a-b)<EPS;
}
typedef complex<double> P;
int main(){

  int t;
  P ans[3];
  cin>>t;
  while(t--){
    double a,b,c,co;
    cin>>co>>a>>b>>c;
    a/=co;b/=co;c/=co;
    double p=(27.0*c+2*a*a*a-9*a*b)/54;
    double q=(3.0*b-a*a)/9;
    double r=-p+sqrt(p*p+q*q*q);
    if(r<0){
      r=abs(r);
      r=pow(r,1.0/3);
      r*=-1;
    }
    else r=pow(r,1.0/3);
    double s=-p-sqrt(p*p+q*q*q);
    if(s<0){
      s=abs(s);
      s=pow(s,1.0/3);
      s*=-1;
    }
    else s=pow(s,1.0/3);
    ans[0]=P(r+s-1.0*a/3,0);
    ans[1]=P(-0.5,sqrt(3.0)/2)*P(r,0)+
      P(-0.5,-sqrt(3.0)/2)*P(s,0)-P(1.0*a/3,0);
    ans[2]=P(-0.5,-sqrt(3.0)/2)*P(r,0)+
      P(-0.5,sqrt(3.0)/2)*P(s,0)-P(1.0*a/3,0);
    int pos=0;
    int neg=0;
    for(int i=0;i<3;i++){
      if(!EQ(ans[i].real(),0)&&EQ(ans[i].imag(),0)){
	if(ans[i].real()>0)pos++;
	else neg++;
      }
    }
    cout<<pos<<" "<<neg<<endl;
  }  

  return 0;
}

