int N;
double a,b,c,d;
double EPS = 1e-6;
double EPS2 = 1e-14;

double func(double x){
    return a*x*x*x + b*x*x + c*x + d;
}

int pl,mn;

bool is_zero(double x){
    if(abs(x) < EPS) return true;
    return false;
}

bool fis_zero(double x){
    if(abs(x) < EPS2) return true;
    return false;
}

void add(double x){
    if(is_zero(x)) return;
    if(x > 0) pl++;
    else if(x < 0) mn++;
    return;
}

void bisearch(double x, double y){
    if(func(y) < func(x)) swap(x,y);
    if(fis_zero(func(x))){ add(x); return;}
    if(fis_zero(func(y))){ add(y); return;}
    double mid;
    int ct = 0;
    rep(i,100){
        mid = (x+y)/2;
        if(fis_zero(func(mid))) break;
        if(func(mid) > 0) y = mid;
        else if(func(mid) < 0) x = mid;
        ct++;
    }
    cout << mid << endl;
    if(ct == 100) return;
    add(mid);
    return;
}

int main(){
    int N;
    cin >> N;
    rep(i,N){
        cin >> a >> b >> c >> d;
        b /= a, c /= a, d /= a, a = 1;
        pl = 0, mn = 0;
        double D = b*b - 3*a*c;
        double xl,xs;

        if(D < 0) bisearch(-10000,10000);
        else{
            xs = (-b-sqrt(D))/(3*a);
            xl = (-b+sqrt(D))/(3*a);
            bisearch(-10000,min(xs,xl));
            bisearch(min(xs,xl),max(xs,xl));
            bisearch(max(xs,xl),10000);
        }

        cout << pl << " " << mn << endl;
    }

}

#include <iostream>
#include <cmath>
using namespace std;
const double EPS = 1e-10;
int m[3] = {0};

int is(double a){
	if(abs(a) < EPS)return 1;
	if(a < 0)return 0;
	if(a > 0)return 2;
}

double mysqrt(double x){
	double l = 0 , r = x;
	for(int i = 0 ; i < 64 ; i++){
		double m = (l+r) / 2.0;
		if(m * m < x) l = m;
		else r = m;
	}
	return l;
}

double solve(double a,double b,double c,double d){
	//cout << a << " " << b << " " << c << " " << d << endl;
	if(a == 0){
		if(c * c - 4 * b * d < -EPS)return 0.0;
		double one = ( -c + mysqrt(c*c-4*b*d)  )/(2*b);
		double two = ( -c - mysqrt(c*c-4*b*d)  )/(2*b);
		//cout << one << "<>" << two << endl;
		m[is(one)]++;
		m[is(two)]++;
		return 0.0;
	}

	double l = -1000.0 , r = 1000.0;

	if(a < 0){ a *= -1; b *= -1; c *= -1; d *= -1;}

	for(int i=0;i<64;i++){
		double x = (l+r)/2.0;
		double res = a*x*x*x+b*x*x+c*x+d;
		if(res>0){
			r = x;
		}else{
			l = x;
		}
	}

	return l;
}
int main(){
	int N; cin >> N;
	while(N--){
		int a,b,c,d;
		cin >> a >> b >> c >> d;
		m[0] = m[1] = m[2] = 0;

		double n = solve(a,b,c,d); // 三次方程式には必ず一つ以上実根が含まれているので、それを見つける。
		m[is(n)]++;

		solve(0,a,a*n+b,n*(a*n+b)+c);
		cout << m[2] << " " << m[0] << endl;
	}
