double sqrt_user(double x) {
    double lb = 0, ub = 10000;
    for(int i=0; i<200; i++) {
        double mid = (lb + ub) / 2.0;
        if(mid * mid < x) lb = mid;
        else ub = mid;
    }
    return lb;
}

