        // printf("D = %.12f\n", c*c - 4*b*d);
        if(c*c - 4*b*d < -EPS) return 0;

        double vl = (-c + sqrt_user(c*c - 4*b*d)) / (2 * b);
        double vr = (-c - sqrt_user(c*c - 4*b*d)) / (2 * b);

        // printf("vl = %.12f, vr = %.12f\n", vl, vr);
        put(vl);
        put(vr);

        return 0;
    }
    else {
        if(a < 0) {
            a = -a, b = -b, c = -c, d = -d;
        }

        double lb = -10000, ub = 10000;
