// find max point [left, right]
double ts(const Polynomial<double> &poly, double left, double right) {
  REP(iter, 300) {
    double lmid = (left * 2.0 + right) / 3.0;
    double rmid = (left + right * 2.0) / 3.0;
    double lans = poly.calc(lmid);
    double rans = poly.calc(rmid);
    assert(!isnan(lans) && !isnan(rans));
    if (lans < rans) {
      left = lmid;
    } else {
      right = rmid;
    }
  }
  return left;
}

// find 0 point [left, right]
// poly must monotonic increase in [left, right]
double bs(const Polynomial<double> &poly, double left, double right) {
  REP(iter, 200) {
    double mid = (left + right) / 2.0;
    double ans = poly.calc(mid);
    if (ans < 0) {
      left = mid;
    } else {
      right = mid;
    }
  }
  return left;
}

pair<int, int> solve1(Polynomial<double> poly) {
  Polynomial<double> dif = -poly.differential();
  double center = ts(dif, -1000, 1000);
  double leftLocalMax = ts(poly, -1000, center);
  double rightLocalMin = ts(-poly, center, 1000);
  double ans[3];
  ans[0] = bs(poly, -1000, leftLocalMax);
  ans[1] = bs(-poly, leftLocalMax, rightLocalMin);
  ans[2] = bs(poly, rightLocalMin, 1000);
  int minusans = 0;
  int plusans = 0;
  REP(i, 3) {
    if (fabs(poly.calc(ans[i])) < EPS) {
      if (ans[i] < -EPS) { minusans++; }
      if (ans[i] > EPS) { plusans++; }
    }
  }
  return make_pair(plusans, minusans);
}

pair<int, int> solve2(Polynomial<double> poly) {
  double ans[3];
  ans[0] = bs(poly, -100, 1000);
  Polynomial<double> dpoly(2, 1);
  dpoly[0] = -ans[0];
  Polynomial<double> npoly = poly.divmod(dpoly).first;
  npoly /= npoly[2];
  double b = npoly[1];
  double c = npoly[0];
  if (fabs(b * b - 4 * c) < EPS) {
    ans[1] = ans[2] = -b / 2.0;
  } else if (b * b - 4 * c < 0) {
    ans[1] = ans[2] = 0;
  } else {
    ans[1] = (-b + sqrt(b * b - 4 * c)) / 2.0;
    ans[2] = (-b - sqrt(b * b - 4 * c)) / 2.0;
  }
  int minusans = 0;
  int plusans = 0;
  REP(i, 3) {
    if (fabs(poly.calc(ans[i])) < EPS) {
      if (ans[i] < -EPS) { minusans++; }
      if (ans[i] > EPS) { plusans++; }
    }
  }
  return make_pair(plusans, minusans);
}

