    Polynomial(int size, T num) : vector<T>(size, num) { }
    Polynomial(vector<T> vec) { *this = vec; }

    Polynomial<T>& normalize() {
        while ( (*this).size() > 1 && abs((*this).back()) <= EPS ) (*this).pop_back();
        return (*this);
    }
    T sub(T x) {
        T res = 0;
        for (int i =  (*this).size()-1; i > 0; --i) res = res + (*this)[i], res = res * x;
        res += (*this)[0];
        return res;
    }
    inline Polynomial<T> operator - () { 
        for (int i = 0; i < (*this).size(); ++i) (*this)[i] = (*this)[i] * (-1); 
        return (*this);
    }
    inline const Polynomial<T>& operator += (const Polynomial<T> &x);
    inline const Polynomial<T>& operator -= (const Polynomial<T> &x);
    inline const Polynomial<T>& operator *= (T x);
