template<class T> pair<Polynomial<T>, Polynomial<T> > divmod(Polynomial<T> x, Polynomial<T> y) {
    if (x.size() < y.size()) return make_pair(0, x);
    Polynomial<T> q((int)x.size() - (int)y.size() + 1, 0);
    for (int i = x.size()-1; i >= y.size()-1; --i) {
        T div = x[i] / y.back();
        q[i-(int)y.size()+1] = div;
        for (int j = 0; j < y.size(); ++j) x[i+j-(int)y.size()+1] = x[i+j-(int)y.size()+1] - y[j] * div;
    }
    return make_pair(q, x.normalize());
}
