vector<long double> quad_eq(long double a, long double b, long double c) {
    vector<long double> res;
    if (abs(a) < EPS) {
        if (abs(b) < EPS) {
            if (abs(c) < EPS) return res;               // all real numbers
            else return res;                            // no solutions
        }
        else return vector<long double>(1, -c/b);
    }
    long double D = b*b - a*c*4;
    if (D > EPS) {
        res.push_back( (-b+sqrt(D))/(a*2) );
        res.push_back( (-b-sqrt(D))/(a*2) );
        return res;
    }
    else if (abs(D) <= EPS) {
        return vector<long double>(2, -b/(a*2));
        //return vector<long double>(1, -b/(a*2));
    }
    else return res;
}



