// 2次方程式 ax^2 + bx + c = 0の解
vector<double> quadratic(double a, double b, double c){
  assert(a != 0);
  double D = b*b - 4*a*c;
  if(D < -EPS) return vector<double>();
  if(D <  EPS) return vector<double>(2, -b/(2.0 * a));

  // |b| >> |ac|の時の桁落ちを避けるために
  // x_1 = (-b-sign(b)*sqrt(D))/(2*a), x_2 = c / (a*x_1)を利用する
  vector<double> res;
  int sign = (b >= 0) ? 1 : -1;
  double x1 = (-b - sign * sqrt(D))/(2.0 * a);
  double x2 = c / (a * x1);
  res.push_back(x1);
  res.push_back(x2);
  return res;
}

