const double EPS = 1.0e-10;

void solveEquation(const vector<double>& a, vector<double>& x)
{
    class Func{
    public:
        static double f(const vector<double>& a, double x){
            double y = 0.0;
            for(int i=a.size()-1; i>=0; --i){
                y *= x;
                y += a[i];
            }
            return y;
        }
        static double findZero(const vector<double>& a, double x1, double x2){
            double y1 = f(a, x1);
            double y2 = f(a, x2);
            while(x1 < x2 - EPS){
                double x = (x1 + x2) / 2;
                double y = f(a, x);
                if((y1 < y2) ^ (y > 0))
                    x1 = x;
                else
                    x2 = x;
            }
            return (x1 + x2) / 2;
        }
    };

    x.clear();
    int n = a.size();
    if(n == 2){
        x.push_back(- a[0] / a[1]);
        return;
    }

    vector<double> b(n-1), x2; // 導関数の係数、極値点のx座標
    for(int i=0; i<n-1; ++i)
        b[i] = a[i+1] * (i+1);
    solveEquation(b, x2);

    const double LIMIT = 1.0e10;
    x2.insert(x2.begin(), -LIMIT);
    x2.push_back(LIMIT);

    for(unsigned i=0; i<x2.size()-1; ++i){
        double y1 = Func::f(a, x2[i]);
        double y2 = Func::f(a, x2[i+1]);
        if(abs(y1) < EPS){
            x.push_back(x2[i]);
        }else if(abs(y2) < EPS){
            x.push_back(x2[i+1]);
        }else if((y1 < 0) ^ (y2 < 0)){
            x.push_back(Func::findZero(a, x2[i], x2[i+1]));
        }
    }
}

int main()
{
    int n;
    cin >> n;

    while(--n >= 0){
        vector<double> a(4);
        for(int i=0; i<4; ++i)
            cin >> a[3-i];

        vector<double> x;
        solveEquation(a, x);

        vector<int> ret(2, 0);
        for(unsigned i=0; i<x.size(); ++i){
            if(x[i] > EPS)
                ++ ret[0];
            else if(x[i] < -EPS)
                ++ ret[1];
        }
        cout << ret[0] << ' ' << ret[1] << endl;
    }

    return 0;
}

