#define double long double
const double EPS = 1e-8;

// 二次方程式を解の公式を用いて解く
// 計算量: O(sqrt()の計算量)
// 重解は1つにせず返す 1つにしたいなら変えること
// Verified:
//   https://www.urionlinejudge.com.br/judge/en/runs/code/13486899
//   http://codeforces.com/contest/233/submission/52143945
vector<double> quadraticEquation(double a, double b, double c) {
	// 1次方程式
	if (abs(a) < EPS)
		return abs(b) < EPS ? vector<double>() : vector<double>{ -c / b };
	// 判別式
	double D = b * b - 4 * a*c;
	// 解無し
	if (D < -EPS)
		return vector<double>();
	// 重解
	if (D < EPS)
		return vector<double>{-b / (2 * a), -b / (2 * a)};
	double d = sqrt(D);
	double x1 = (-b + d) / (2 * a);
	double x2 = (-b - d) / (2 * a);
	if (x1 > x2)swap(x1, x2);
	return vector<double>{x1, x2};
}

// 二分法
// 方程式の求根アルゴリズム
// f(l) < 0 かつ 0 < f(r) の場合に限る
double bisectionMethod(double l, double r, function<double(double)> f) {
	double yl = f(l), yr = f(r);
	if (yl > yr)swap(l, r);
	assert((yl - EPS) * (yr + EPS) <= 0);
	for (int i = 0; i < 200; i++) {
		double m = (l + r) / 2;
		if (f(m) > 0)
			r = m;
		else
			l = m;
	}
	return l;
}

// 三次方程式
// 二分法でまず1つの解pを求める
// (x-p)(ax^2+b'x+c) と式変形して二次方程式で残りの解を求める
// 計算量: 二分法の計算量
// Verified: https://yukicoder.me/submissions/331206
vector<double> cubicEquation(double a, double b, double c, double d) {
	if (abs(a) < EPS)
		return quadraticEquation(b, c, d);
	double p = bisectionMethod(-INF, INF, [&](double x) {return a * x*x*x + b * x*x + c * x + d; });
	// (1, b/a, c/a, d/a) を (1, -p) で割っていった結果
	double aa = 1, bb = b / a + p, cc = c / a + bb * p;
	auto ret = quadraticEquation(aa, bb, cc);
	ret.push_back(p);
	sort(ret.begin(), ret.end());
	return ret;
}

signed main() {
	cin.tie(0);
	ios::sync_with_stdio(false);
	int t; cin >> t;
	rep(_, 0, t) {
		int a, b, c, d; cin >> a >> b >> c >> d;
		auto r = cubicEquation(a, b, c, d);
		dump(r);
		int p = 0, m = 0;
		for (auto x : r) {
			if (abs(x) < EPS)continue;
			if (x > 0)p++;
			else m++;
		}
		cout << p << " " << m << endl;
	}
	return 0;
}

